INSERT INTO message (id, content, created_at, report_counter, blocked, id_product, id_non_admin) VALUES ('9290', 'Getting a no when she was auditioning on the X Factor must have been the best thing that could have happened to Chrissie otherwise this album may not have happened and that would have been a loss. Together with Ward Thomas, The Shires have put British country music well and truly in the mix.  This really is an excellent collection of catchy tunes that I will be listening to for a long time to come. Don"t hesitate,  buy it!!', '2018-06-27T20:33:50+01:00', '0', 'false', '591', '1085') ;
